#include "stdlib/math/base/special/atan2.h"
#include "stdlib/random/base/randu.h"
#include <stdio.h>

int main( void ) {
    double y;
    double x;
    int i;

    for ( i = 0; i < 100; i++ ) {
        y = stdlib_base_random_randu(100);
        x = stdlib_base_random_randu(100);
        printf("y: %.4f, \t x: %.4f, \t atan2(y,x): %.4f\n", y, x, stdlib_base_atan2(y, x));
    }
    return 0;
}